import React from 'react'
import { Button } from 'reactstrap';



class CommentBox extends React.Component {
  state = {open: false}

  handleChange = () => {
    this.props.dataSet.checked = !this.props.dataSet.checked;
  
    this.setState(prevStated => ({
      open: !prevStated.open
    }))
   
    
  }
  
  
render() {  

  const button = this.props.dataSet.checked ?
 
     <input type="checkbox" onChange={this.handleChange} checked={true}  />
   :
    <input type="checkbox" onChange={this.handleChange} checked={false}  />

  return(
    <div className="form-group">
    <h1>Uppgift: {this.props.dataSet.details}</h1>
    {button}
    <br></br><br></br>
    <Button color="primary" size="sm"  onClick={() => this.props.handleClick()}>Go vidare</Button>
    <br></br><br></br>
    <Button color="primary" size="sm"  onClick={() => this.props.handleSubmit()}>Spara framsteg</Button>
    {this.state.open = false}
    
  </div>
 
  )

}
 

 
}

export default Step
