import { useOktaAuth } from "@okta/okta-react";
import React, { useState, useEffect, Fragment } from "react";
import { connect } from 'react-redux'
import { clearProduct } from '../components/RemoveProduct'
import '../components/cart.css'

const Cart = ({basketProps,clearProduct}) => {
  const { authState, authService } = useOktaAuth();
  const [userInfo, setUserInfo] = useState(null);
  let productsInCart = [];

  const handleSubmit = (event) => {
    event.preventDefault();
    const inputs = event.target.getElementsByTagName('input')
    console.log(inputs.username.value)
    fetch('/review/api/v1/reviews', {
        method: 'POST',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
            bookId: inputs.bookId.value, 
            username: inputs.username.value, 
            comment:inputs.text.value,
            points: inputs.points.value,
            date: "2020-10-12"
        })
      })
      .then(response => console.log(response))
      .then(window.location.reload())
   
      
  }
  


  productsInCart = basketProps.map(item=> {
    return (
        <Fragment key={item.id}>
        <div className="product"><ion-icon onClick={() => clearProduct(item.name)} name="close-circle"></ion-icon><img src={item.img} />
        <span className="sm-hide">{item.name}</span>
        </div>
        </Fragment>
    )
    
  });

return (
    <div className="products-container">
        <div className="product-header">
            <h5 className="product-title">PRODUCT</h5>
        </div>
        <div className="products">
        {productsInCart}
        </div>
    </div>
)
 
}
const mapStateToProps = state => ({
    basketProps: state.basketState
});

export default connect(mapStateToProps, {clearProduct})(Cart);
