import { useOktaAuth } from "@okta/okta-react";
import React, { useState, useEffect, Fragment } from "react";
import { connect } from 'react-redux'
import '../components/cart.css'

const Cart = ({basketProps}) => {
  const { authState, authService } = useOktaAuth();
  const [userInfo, setUserInfo] = useState(null);
  let productsInCart = [];
  


  productsInCart = basketProps.map(item=> {
    
    return (
        <Fragment key={item.id}>
        <div className="product"><ion-icon name="close-circle"></ion-icon><img src={item.img} />
        <span className="sm-hide">{item.name}</span>
        </div>
        <div className="price sm-hide">..</div>
        <div className="quantity" >
        <ion-icon className="decrease" name="arrow-back-circle-outline"></ion-icon>
        <span>NUMBERS</span>
        <ion-icon className="increase" name="arrow-forward-circle-outline"></ion-icon>
        </div>
        <div className="total">..</div>
        </Fragment>
    )
    
  });

return (
    <div className="container-products">
        <div className="product-header">
            <h5 className="product-title">PRODUCT</h5>
            <h5 className="price sm-hide">PRICE</h5>
            <h5 className="quantity">QUANTITY</h5>
            <h5 className="total">TOTAL</h5>
        </div>
        <div className="products">
        {productsInCart}
        </div>
       <div className="basketTotalContainer"></div>
       <h4 className="basketTotalTitle">Basket Total</h4>
       <h4 className="basketTotal">TOTAL</h4>
    </div>
)
 
}
const mapStateToProps = state => ({
    basketProps: state.basketState
});

export default connect(mapStateToProps)(Cart);
