import React, {useState, Component } from 'react';
import { Link } from 'react-router-dom';
import Navbar from "../navbar/Navbar"
import './BookDetail.css'

class BookDetails extends Component {
    constructor(props) {
        super(props);
        this.state = {data: [], isLoading: true, current: 0,complete: false};   
    }
    componentDidMount() {
        this.setState({isLoading: true});
      
        fetch(`catalog/api/v1/books/all`)
          .then(response => response.json())
          .then(dataSet => this.setState({data: dataSet, isLoading: false}))
          .catch(() => this.props.history.push('/')); 
      }



    render () {

        return (
            
            <div className="container">
               <div className="left-column">
               <img src="https://images-eu.ssl-images-amazon.com/images/I/91k%2B0cXtqOL._AC_UL320_SR208,320_.jpg"></img>
               <button>Borrow</button>
               </div>
               <div class="right-column">
               <div class="product-description">
               <div class="rating">
							<div class="stars">
								<span class="fa fa-star checked"></span>
								<span class="fa fa-star checked"></span>
								<span class="fa fa-star checked"></span>
								<span class="fa fa-star"></span>
								<span class="fa fa-star"></span>
							</div>
							<span class="review-no">41 reviews</span>
						</div>
              
            
                     <h1>Harry Potter</h1>
                     <br></br>
                     
                    <p>The preferred choice of a vast range of acclaimed DJs. Punchy, bass-focused sound and high isolation. Sturdy headband and on-ear cushions suitable for live performance</p>
                    
             </div>
             <div class="bookDetailsNew__authorsList">
                        <span>
                            <p>Förtfattare:</p><a href="/författare/samuel-karlsson-68247">Samuel Karlsson</a>
                        </span>
                    </div>
             
    </div>
  
  </div>   
            
        )
    }

    }
    export default BookDetails;
