import React, {useState, Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import './Home.css'
import styled from "styled-components";

const Button = styled.button`
  background: white;
  color:red;
  font-size: 1em;
  margin: 1em;
  padding: 0.25em 1em;
  border: 2px solid red;
  border-radius: 3px;
`


class Home extends Component {
    
  constructor(props) {
    super(props);
    this.state = {data: [], isLoading: true, current: 0,complete: false};
  
    
}
componentDidMount() {
  this.setState({isLoading: true});

  fetch(`catalog/api/v1/books/all`)
    .then(response => response.json())
    .then(dataSet => this.setState({data: dataSet, isLoading: false}))
    .catch(() => this.props.history.push('/'));


  
}
 
  render() { 
    const {data,isLoading} = this.state;
    if(isLoading) {
      return <p>Loading.....</p>
     
    }
    
    return (
      <div className="app">
      <div id="product">
        {data.map(books => (
          <div className="card" key={books.bookId}>
          <img src="/images/test.jpg"></img>
            <div className="content">
              <h3>
                {books.title}
              </h3>
              <span>Score: {books.averageScore}</span>
              <Button as={Link} to="/home"> Text Goes Here </Button>
            </div>
          </div>
        ))}
          
        </div>
        </div>
    
      )};
      }
    
    export default Home
