import React, {useState, Component } from 'react';
import { Link } from 'react-router-dom';
import Navbar from "../navbar/Navbar"
import { withOktaAuth,useOktaAuth } from '@okta/okta-react';


export default withOktaAuth(class Home extends Component {
    
  constructor(props) {
    super(props);
    this.state = { userInfo: null };
    this.checkUser = checkUser.bind(this);
    this.login = this.login.bind(this);
    this.logout = this.logout.bind(this);
    
  }

  async login() {
    this.props.authService.login('/');
  }

  async logout() {
    this.props.authService.logout('/');
  }


  async componentDidMount() {
    this.checkUser();
  }

  async componentDidUpdate() {
    this.checkUser();
  }
  

 

  render() {  
    console.log(this.props.authState.isAuthenticated )
    
    const button = this.props.authState.isAuthenticated ?
      <button onClick={this.logout}>Logout</button> :
      <button onClick={this.login}>Login</button>;

    return (
      <div>
        <Navbar userSession={this.props.authState.isAuthenticated} />
        <div>
        {this.state.userInfo &&
          <div>
            <p>Welcome back, {this.state.userInfo.name} AND {this.accessToken}!</p>
          </div>
        }
      </div>
          <p>WELCOME SIR! </p>
        <Link to='/'>Home</Link><br/>
        {button}
      </div>
      
    );
  }
});
