import React, {useState, Component } from 'react';
import { Link } from 'react-router-dom';
import CommentBox from '../CommentBox';
import Navbar from "../navbar/Navbar"
import './BookDetail.css'
import { withOktaAuth } from '@okta/okta-react';
import CommentField from './CommentField';


async function checkUser() {
  if (this.props.authState.isAuthenticated && !this.state.userInfo) {
    const userInfo = await this.props.authService.getUser();
    if (this._isMounted) {
      this.setState({ userInfo });
    }
  }
}

export default withOktaAuth(class BookDetails extends Component {
  _isMounted = false;
    constructor(props) {
        super(props);
        this.state = {data: [], isLoading: true, current: 0,complete: false,userInfo: null };   
        this.checkUser = checkUser.bind(this);
        
    }
    componentDidMount() {
      this._isMounted = true;
        this.setState({isLoading: true});
        this.checkUser();

        try {
            fetch(`/catalog/api/v1/books/${this.props.match.params.id}`)
            .then(response => response.json())
            .then(dataSet => this.setState({data: dataSet, isLoading: false}))
            .catch(() => this.props.history.push('/')); 
          } catch (error) {
            this.props.history.push('/');
          }
      }

    render () {
    const {data,isLoading,userInfo} = this.state;
    console.log(userInfo);
    if(isLoading) {
      return <p>Loading.....</p>
     
    }
  


        return (
          
            <div>
            
              {this.props.authState.isAuthenticated ? <CommentField bookId={data.bookId} points={2}/>:null}
                <div className="container">
               <div className="left-column">
               <img src={data.imageLink}></img>
               <button>Borrow</button>
               </div>
               <div className="right-column">
               <div className="product-description">
                  <h1>{data.title}</h1>
                  <br></br>
                     
                    <p>Description here</p>
                    
             </div>
             <div className="bookDetailsNew__authorsList">
                        <span>
                          <p>FÃ¶rtfattare:</p>
                            {data.authors.map(authors => (
                                <a href="#" key={authors.id}>{authors.name}</a>
                            ))}     
                        </span>
                    </div>
             </div>
             
        </div>
        <CommentBox cmnts={data.reviews}></CommentBox> 
            </div>
           
            
        )
    }

    });
