import { ADD_PRODUCT_BASKET, GET_NUMBERS_IN_BASKET } from "../actions/types";

const initialState = {
    addedIds: [],
    addedItems: [],
}

   const basketReducer = (state = initialState.addedIds, action) => {
    switch(action.type) {
        case ADD_PRODUCT_BASKET:
            if (state.indexOf(action.bookId) !== -1) {
                return state
              }
          
              return {
                addedIds:   [ ...state, action.bookId],
                addedItems: [ ...state, action.name],
              }
        case GET_NUMBERS_IN_BASKET:
            return {
                ...state
            }
        default:
            return state

    }
}

    
export default basketReducer;
