import {ADD_PRODUCT_BASKET} from './types';

export const addBasket = (bookId,name) =>{
    return (dispatch) => {
        console.log("Adding to basket.")
        dispatch({
            type:ADD_PRODUCT_BASKET,
            bookId:bookId
        })
    }
};
