import { ADD_PRODUCT_BASKET, GET_NUMBERS_IN_BASKET } from "../actions/types";

const initialState = {
    addedItems: [],
}

   const basketReducer = (state = initialState, action) => {
    switch(action.type) {
        case ADD_PRODUCT_BASKET:
             let existed_item= state.addedItems.find(item=> action.id === item.id)
          
              return {
                addedItems: [ state.addedItems, action.name],
              }
        case GET_NUMBERS_IN_BASKET:
            return {
                ...state
            }
        default:
            return state

    }
}

    
export default basketReducer;
