import React, { useState, Component  } from 'react'
import { MenuItems } from "./navbar/MenuItems"
import { withOktaAuth, useOktaAuth } from '@okta/okta-react';
import './Navbar.css'
import { Navbar } from 'reactstrap';
import AuthInformation from './auth/AuthInformation'



async function checkUser() {
    if (this.props.authState.isAuthenticated && !this.state.userInfo) {
      const userInfo = await this.props.authService.getUser();
      this.setState({ userInfo });
    }
  }


  export default withOktaAuth(class Navbar extends Component {
    constructor(props) {
        super(props);
        this.state = { userInfo: null};
        this.checkUser = checkUser.bind(this);
        this.login = this.login.bind(this);
        this.logout = this.logout.bind(this);
        
      }

      async login() {
        this.props.authService.login('/');
      }
    
      async logout() {
        this.props.authService.logout('/');
      }

      async componentDidMount() {
        this.checkUser();
      }
    
      async componentDidUpdate() {
        this.checkUser();
      }
      
      async checkAuthentication() {
        const authenticated = await this.props.auth.isAuthenticated();
        if (authenticated !== this.state.authenticated) {
            this.setState({ authenticated });
        }
    }

    handleClick = () => {
        this.setState({clicked: !this.state.clicked})
    }
   
    render() {
      console.log(AuthInformation.authState.isAuthenticated);
        const button = this.props.authState.isAuthenticated ?
        <button onClick={this.logout}>Logout</button> :
        <button onClick={this.login}>Login</button>;
        

        return (
            <nav className="NavBarItems">
               <a href="/"> <h1 className="navbar-logo">Library<i className="fab fa-react"></i></h1></a>
                <div className="menu-icon">
                    <i onClick={this.handleClick} className={this.state.clicked ? 'fas fa-times': 'fas fa-bars'}></i>
                </div>
                <ul className={this.state.clicked ? 'nav-menu active' : 'nav-menu'}>
                  <li><a className="nav-links" href="/">Books</a></li>
                  <li><a className="nav-links" href="/cart">Cart<span>(0)</span></a></li>
                  
                 {button}
                    
                </ul>
            </nav>
        )
    }
});
