import React, {useState, Component } from 'react';
import './Home.css'




class Home extends Component {
    
  constructor(props) {
    super(props);
    this.state = {data: [], isLoading: true, current: 0,complete: false};
  
    
}
componentDidMount() {
  this.setState({isLoading: true});

  fetch(`catalog/api/v1/books/all`)
    .then(response => response.json())
    .then(dataSet => this.setState({data: dataSet, isLoading: false}))
    .catch(() => this.props.history.push('/'));


  
}
 
  render() { 
    const {data,isLoading} = this.state;
    if(isLoading) {
      return <p>Loading.....</p>
     
    }
    
    return (
      <div className="app">
      <div id="product">
        {data.map(books => (
          <div className="card" key={books.id}>
          <img src="/images/test.jpg"></img>
            <div className="content">
              <h3>
                {books.title}
              </h3>
              <span>Pages: {books.pages}</span>
              <p>{books.year}</p>
              <button>Borrow</button>
            </div>
          </div>
        ))}
          
        </div>
        </div>
    
      )};
      }
    
    export default Home
