import React from 'react'
import '../pages/commentfield.css'
class CommentField extends React.Component {
    constructor(props) {
        super(props)
        this.state = { username: '',test:'' }
        this.handleSubmit = this.handleSubmit.bind(this)
      }

    async handleSubmit(event) {
        event.preventDefault();
        const inputs = event.target.getElementsByTagName('input')
       
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ bookId: inputs.bookId.value, userName: inputs.username.value, comment:inputs.name.value, points: inputs.points.value, date: '2020-10-12'})
        };
        fetch('https://jsonplaceholder.typicode.com/posts', requestOptions)
            .then(response => response.json())
      }

    render() {  
    
      return(
          <div>
        <form className="commentForm" onSubmit={this.handleSubmit}>
        <input type="hidden" name="bookId" value={this.props.bookId}/>
        <input type="hidden" name="points" value={this.props.points}/>
        <input type="text" name="username" placeholder="Your name"/>
        <input type="text" name="text" placeholder="Say something..."/>
        <input type="submit" value="Post" />
      </form>
          </div>
         
     
      )
    
    }
     
    
     
    }
    
    export default CommentField
