import React, {useState, Component } from 'react';
import { Link } from 'react-router-dom';
import CommentBox from '../CommentBox';
import Navbar from "../navbar/Navbar"
import './BookDetail.css'
import { withAuth } from '@okta/okta-react';
import { useAuth } from '../auth/Auth';

export default withOktaAuth(class BookDetails extends Component {
    constructor(props) {
        super(props);
        this.state = {data: [], isLoading: true, current: 0,complete: false};   
        const [authenticated, user] = useAuth(auth);
        
    }
    componentDidMount() {
        this.setState({isLoading: true});

        try {
            fetch(`/catalog/api/v1/books/${this.props.match.params.id}`)
            .then(response => response.json())
            .then(dataSet => this.setState({data: dataSet, isLoading: false}))
            .catch(() => this.props.history.push('/')); 
          } catch (error) {
            this.props.history.push('/');
          }
      }

    render () {
    const {data,isLoading} = this.state;
    if(isLoading) {
      return <p>Loading.....</p>
     
    }
  

        return (
            <div>
                <div className="container">
               <div className="left-column">
               <img src={data.imageLink}></img>
               <button>Borrow</button>
               </div>
               <div class="right-column">
               <div class="product-description">
               <div class="rating">
							<div class="stars">
								<span class="fa fa-star checked"></span>
								<span class="fa fa-star checked"></span>
								<span class="fa fa-star checked"></span>
								<span class="fa fa-star"></span>
								<span class="fa fa-star"></span>
							</div>
						</div>
              
            
                     <h1>{data.title}</h1>
                     <br></br>
                     
                    <p>Description here</p>
                    
             </div>
             <div class="bookDetailsNew__authorsList">
                        <span>
                          <p>Förtfattare:</p>
                            {data.authors.map(authors => (
                                <a href="#" key={authors.id}>{authors.name}</a>
                            ))}     
                        </span>
                    </div>
             </div>
             
        </div>
        <CommentBox cmnts={data.reviews}></CommentBox> 
            </div>
           
            
        )
    }

    });
