import React, { useState, Component,useEffect  } from 'react'
import './Navbar.css'
import { connect } from 'react-redux'
import { getNumbers } from '../actions/getAction'
import AuthInformation from './auth/AuthInformation'

const mapStateToProps = state => ({
  basketProps: state.basketProps
})

  class Navbar extends Component {
    constructor(props) {
        super(props);
        this.state = { userInfo: null};
    
      }
     

    handleClick = () => {
        this.setState({clicked: !this.state.clicked})
    }

    useEffect = () => {
        getNumbers()
    };
   
    render() {
      console.log(this.props)
        return (
            <nav className="NavBarItems">
               <a href="/"> <h1 className="navbar-logo">Library<i className="fab fa-react"></i></h1></a>
                <div className="menu-icon">
                    <i onClick={this.handleClick} className={this.state.clicked ? 'fas fa-times': 'fas fa-bars'}></i>
                </div>
                <ul className={this.state.clicked ? 'nav-menu active' : 'nav-menu'}>
                  <li><a className="nav-links" href="/">Books</a></li>
                  <li><a className="nav-links" href="/cart">Cart<span>{this.props.basketProps}</span></a></li>
                  
                    <AuthInformation />
                </ul>
            </nav>
        )
    }
    
}



export default connect(mapStateToProps, {getNumbers})(Navbar)
