import { ADD_PRODUCT_BASKET, GET_NUMBERS_IN_BASKET } from "../actions/types";

const initialState = {
    basketNumbers: 0,
    addedIds: [],
}

   const basketReducer = (state = initialState.addedIds, action) => {
    switch(action.type) {
        case ADD_PRODUCT_BASKET:
            console.log(action.payload)
            if (state.addedIds.indexOf(action.payload) !== -1) {
                return state
              }
              return [ ...state.addedIds, action.payload ]
        case GET_NUMBERS_IN_BASKET:
            return {
                ...state
            }
        default:
            return state

    }
}
export default basketReducer;
