import { ADD_PRODUCT_BASKET, GET_NUMBERS_IN_BASKET } from "../actions/types";

const initialState = {
    addedItems: [],
}

   const basketReducer = (state = initialState.addedItems, action) => {
    switch(action.type) {
        case ADD_PRODUCT_BASKET:
            console.log(action.product.id)
            let existed_item= state.find(item=>action.id === item.id)
            console.log(existed_item + " lel")
            if(existed_item) {
                console.log("KEK")
                return state;
            }
           
              return [ ...state, action.product]
        case GET_NUMBERS_IN_BASKET:
            return {
                ...state
            }
        default:
            return state

    }
}

    
export default basketReducer;
